(992, 61, 'add_country_3_content_section', '<span style=\"color: #ff0000;\"><span style=\"font-size: large;\"><b>Documents forBelgium Visa Application</b></span></span>\r\n<ol>\r\n 	<li><span style=\"color: #000000;\">Original Valid passport for 06 months from the date of arrival in Belgium + Old passport.</span></li>\r\n 	<li><span style=\"color: #000000;\">2 photographs : 3.5X4.5mm, white background matt finish, 80% face size.</span></li>\r\n 	<li><span style=\"color: #000000;\">Confirm Air tickets.</span></li>\r\n 	<li><span style=\"color: #000000;\">Travel Insurance</span></li>\r\n 	<li><span style=\"color: #000000;\">Income tax returns or Form 16 for last 3 years.</span></li>\r\n 	<li><span style=\"color: #000000;\">Original personal bank statements for last 6 months with healthy and sufficient balance.</span></li>\r\n 	<li><span style=\"color: #000000;\">Salary Slips for last 3 months - if employed</span></li>\r\n 	<li><span style=\"color: #000000;\">Original Leave letter from Employer/School/College. hotel booking.</span></li>\r\n 	<li><span style=\"color: #000000;\">If Self Employed require Shop Act/MOA/Deed.</span></li>\r\n</ol>\r\n<span style=\"color: #000000;\">Documents if Invited by Friends / Family:</span>\r\n<ol>\r\n 	<li><span style=\"color: #000000;\">Passport &amp; Visa copies.</span></li>\r\n 	<li><span style=\"color: #000000;\">Invitation letter from the host in Belgium.</span></li>\r\n 	<li><span style=\"color: #000000;\">1 Accommodation proof in Belgium.</span></li>\r\n 	<li><span style=\"color: #000000;\">Relationship certificate if visiting family.</span></li>\r\n</ol>'),
