(1010, 61, 'add_country_6_content_section', '<span style=\"color: #000000;\">China submission required documents for Tourist visa</span>\r\n\r\n<span style=\"color: #000000;\">1. Application form</span>\r\n\r\n<span style=\"color: #000000;\">2. Cover letter</span>\r\n\r\n<span style=\"color: #000000;\">3. Ticket and hotel confirmed</span>\r\n\r\n<span style=\"color: #000000;\">4. Itinerary</span>\r\n\r\n<span style=\"color: #000000;\">5. Bank statement 6 month stamped and signed from bank with 1 lakh 50 thousand closing balance</span>\r\n\r\n<span style=\"color: #000000;\">6. Passport</span>\r\n\r\n<span style=\"color: #000000;\">7. Passport Xerox front and back</span>\r\n\r\n<span style=\"color: #000000;\">8. Last china visa Xerox</span>\r\n\r\n<span style=\"color: #000000;\">9. Two copy photo and photo soft copy</span>\r\n\r\n<span style=\"color: #000000;\"><b>China submission required documents for Business visa</b></span>\r\n\r\n<span style=\"color: #000000;\">1. Application form</span>\r\n\r\n<span style=\"color: #000000;\">2. Covering letter</span>\r\n\r\n<span style=\"color: #000000;\">3. Invitation letter</span>\r\n\r\n<span style=\"color: #000000;\">4. Company registration (Chinese)</span>\r\n\r\n<span style=\"color: #000000;\">5. Inviters I\'d (front and back)</span>\r\n\r\n<span style=\"color: #000000;\">6. Gst certificate (Indian company)</span>\r\n\r\n<span style=\"color: #000000;\">7. Passport</span>\r\n\r\n<span style=\"color: #000000;\">8. Old (if has) and new passport front and back Xerox</span>\r\n\r\n<span style=\"color: #000000;\">And last visa Xerox</span>\r\n\r\n<span style=\"color: #000000;\">9. Two copies of the photo</span>'),
